function sendChat(){const chatInput=document.getElementById("chatInput"),message=chatInput.value.trim();if(""!==message){const chatBox=document.getElementById("chatBubble"),chatScroll=document.getElementById("chatScroll");function scrollDown(){chatScroll.scrollTop=document.body.scrollHeight}chatBox.innerHTML+='<div class="chat_list"><div class="chat_bubble"><div class="chat_bubble_box"><div class="chat_bubble_box-txt"><p>'+message+"</p></div></div></div></div>",scrollDown(),chatInput.value=""}}function handleKeyPress(event){"Enter"===event.key&&sendChat()}document.addEventListener("DOMContentLoaded",(function(){var floatChat=document.querySelector(".float_chat"),newX=16,newY=32;floatChat.style.position="fixed",floatChat.style.right="16px",floatChat.style.bottom="32px"})),document.addEventListener("DOMContentLoaded",(function(){var btnChatOpen=document.querySelector(".btn-chat_open"),chatLayer=document.querySelector(".chat_layer");btnChatOpen.addEventListener("click",(function(){chatLayer.classList.toggle("on")}))})),document.getElementById("chatInput").addEventListener("keydown",handleKeyPress);